import React, { useState, useEffect } from 'react';

function App() {
    // State variables
    const [messages, setMessages] = useState([]); // Stores chat history
    const [input, setInput] = useState('');       // Stores user input
    const [listening, setListening] = useState(false); // To track listening state

    // Function to handle sending the message
    const handleSend = async () => {
        if (input.trim()) {
            // Add user's message to the chat history
            setMessages([...messages, { sender: "User", text: input }]);
            // Clear the input field
            setInput("");

            // Send message to the backend and receive a response
            try {
                const response = await fetch("http://127.0.0.1:5000/chat", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({ message: input })
                });
                const data = await response.json();
                const botResponse = data.response;

                // Add bot response to the chat history
                setMessages((prevMessages) => [
                    ...prevMessages,
                    { sender: "Bot", text: botResponse }
                ]);

                // Convert bot response to speech
                speak(botResponse);
            } catch (error) {
                console.error("Error communicating with backend:", error);
            }
        }
    };

    // Function to handle "Enter" key press to send the message
    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            handleSend();
        }
    };

    // Voice recognition setup
    const startListening = () => {
        const recognition = new window.webkitSpeechRecognition();
        recognition.lang = 'en-US';
        recognition.interimResults = false;
        recognition.onresult = (event) => {
            const transcript = event.results[0][0].transcript;
            setInput(transcript);
            setListening(false);
        };
        recognition.onerror = (event) => {
            console.error("Speech recognition error", event.error);
            setListening(false);
        };
        setListening(true);
        recognition.start();
    };

    // Text-to-Speech (Speech synthesis)
    const speak = (text) => {
        const utterance = new SpeechSynthesisUtterance(text);
        window.speechSynthesis.speak(utterance);
    };

    return (
        <div style={{ padding: '20px', maxWidth: '600px', margin: 'auto', fontFamily: 'Arial, sans-serif' }}>
            <h2>AI Chatbot</h2>

            {/* Chat Messages */}
            <div style={{
                border: '1px solid #ccc',
                borderRadius: '8px',
                padding: '15px',
                height: '400px',
                overflowY: 'auto',
                marginBottom: '20px'
            }}>
                {messages.map((message, index) => (
                    <div key={index} style={{
                        marginBottom: '10px',
                        textAlign: message.sender === 'User' ? 'right' : 'left'
                    }}>
                        <strong>{message.sender}:</strong> {message.text}
                    </div>
                ))}
            </div>

            {/* Input, Send Button, and Voice Button */}
            <div style={{ display: 'flex', alignItems: 'center' }}>
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Type your message"
                    style={{
                        flex: '1',
                        padding: '10px',
                        borderRadius: '4px',
                        border: '1px solid #ccc',
                        marginRight: '10px'
                    }}
                />
                <button onClick={handleSend} style={{ padding: '10px 20px' }}>Send</button>
                <button onClick={startListening} style={{
                    padding: '10px',
                    marginLeft: '10px',
                    backgroundColor: listening ? 'lightcoral' : 'lightblue'
                }}>
                    🎤 {listening ? "Listening..." : "Voice"}
                </button>
            </div>
        </div>
    );
}

export default App;
